SET SQL_SAFE_UPDATES = 0;
SET FOREIGN_KEY_CHECKS=0;
DELETE FROM reservation WHERE 1=1;


SET FOREIGN_KEY_CHECKS=1;

SET SQL_SAFE_UPDATES = 0;
SET FOREIGN_KEY_CHECKS=0;
DELETE FROM reservation_item WHERE 1=1;
SET FOREIGN_KEY_CHECKS=1;

with r as (select reservation_id, status from reservation),
i as (select item_id, item_model_id from item),
m as (select item_model_id from item),
i1 as (select reservation_id, (select i.item_id from i order by RAND() LIMIT 1) as item_id, NULL as item_model_id
from r where r.status IN("RETURNED","OUT")
order by reservation_id),
i2 as(select reservation_id, (select i.item_id from i order by RAND() LIMIT 1) as item_id, NULL as item_model_id
from r where r.status IN("RETURNED","OUT")
order by RAND() LIMIT 100),
i3 as(select reservation_id, (select i.item_id from i order by RAND() LIMIT 1) as item_id, NULL as item_model_id
from r where r.status IN("RETURNED","OUT")
order by RAND() LIMIT 50),
c1 as(select reservation_id, NULL as item_id, (select i.item_model_id from i order by RAND() LIMIT 1) as item_model_id
from r where r.status = "RESERVED"
order by reservation_id),
c2 as(select reservation_id, NULL as item_id, (select i.item_model_id from i order by RAND() LIMIT 1) as item_model_id
from r where r.status = "RESERVED"
order by RAND() LIMIT 10),
c3 as(select reservation_id, NULL as item_id, (select i.item_model_id from i order by RAND() LIMIT 1) as item_model_id
from r where r.status = "RESERVED"
order by RAND() LIMIT 5),
results as(
select * from i1
union
select * from i2
union
select * from i3
union
select * from c1
union
select * from c2
union
select * from c3)
select * from results order by item_model_id;

update reservation_item
set item_model_id = (select item_model_id from item where item_id = reservation_item.item_id)
where item_model_id is null and item_id is not null;

